Shows how to print the User Id's of the ADK keys of an OpenPGP key

 // Assuming keyStore is an instance of a DidiSoft.Pgp.KeyStore
 for (int i=0; i < keyStore.GetAdkKeysCount(publicKeyUserId); i++)
 {
    string adkKeyFingreprint = keyStore.GetAdkKeyFingerprint(publicKeyUserId, i);
    long adkKeyId = keyStore.GetKeyIdForFingerprint(adkKeyFingreprint);
    if (adkKeyId != -1) 
    {
      KeyPairInformation adkKey = keyStore.GetKey(adkKeyId);
      Console.WriteLine("ADK key " + i + " User ID is " + adkKey.UserId);
    } else {
      Console.WriteLine("The ADK key with fingerprint " + adkKeyFingreprint + " is missing from this KeyStore");
    }
 }

' Assuming keyStore is an instance of a DidiSoft.Pgp.KeyStore
For i As Integer = 0 To keyStore.GetAdkKeysCount(publicKeyUserId) - 1
	Dim adkKeyFingreprint As String = keyStore.GetAdkKeyFingerprint(publicKeyUserId, i)
	Dim adkKeyId As Long = keyStore.GetKeyIdForFingerprint(adkKeyFingreprint)
	If adkKeyId &lt;&gt; -1 Then
		Dim adkKey As KeyPairInformation = keyStore.GetKey(adkKeyId)
		Console.WriteLine(("ADK key " & i & " User ID is ") + adkKey.UserId)
	Else
		Console.WriteLine("The ADK key with fingerprint " & adkKeyFingreprint & " is missing from this KeyStore")
	End If
Next

