using System;
using DidiSoft.Pgp;

public class AddEncryptionSubKey
{
 public static void Demo()
 {
  // initialize the key store
  KeyStore keyStore = new KeyStore(@"DataFiles\key.store", "changeit");

  // User Id of the signing key, this is usually our private key
  String signUserId = "support@didisoft.com";
  String privateKeyPassword = "changeit";

  // add ECC sub key
  KeyPairInformation.SubKey newKey2 = keyStore.AddEncryptionSubKey(signUserId, privateKeyPassword, EcCurve.P521);
 }
}

using System;
using DidiSoft.Pgp;

public class AddEncryptionSubKey
{
 public static void Demo()
 {
  // initialize the key store
  KeyStore keyStore = new KeyStore(@"DataFiles\key.store", "changeit");

  // User Id of the signing key, this is usually our private key
  String signUserId = "support@didisoft.com";
  String privateKeyPassword = "changeit";

  // add ECC sub key
  KeyPairInformation.SubKey newKey2 = keyStore.AddSigningSubKey(signUserId, privateKeyPassword, EcCurve.P521);
 }
}

using System;
using DidiSoft.Pgp;

public class AddEncryptionSubKey
{
 public static void Demo()
 {
  // initialize the key store
  KeyStore keyStore = new KeyStore(@"DataFiles\key.store", "changeit");

  // User Id of the signing key, this is usually our private key
  String signUserId = "support@didisoft.com";
  String privateKeyPassword = "changeit";

  // add RSA sub key
  KeyPairInformation.SubKey newKey1 = keyStore.AddEncryptionSubKey(signUserId, privateKeyPassword, KeyAlgorithm.RSA, 2048);
 }
}

