member.example:
This example illustrates how to create an OpenPGP key pair that can be exported afterwards without using a KeyStore object.
using System; using System.IO; using DidiSoft.Pgp;
class GenerateKeyPairWithoutKS { public void Demo() { String userId = "rsa_demo@didisoft.com"; String privateKeyPassword = "private key password";
HashAlgorithm[] hashing = { HashAlgorithm.SHA1, HashAlgorithm.MD5, HashAlgorithm.SHA256, HashAlgorithm.SHA384, HashAlgorithm.SHA512};
CompressionAlgorithm[] compression = { CompressionAlgorithm.ZIP, CompressionAlgorithm.ZLIB, CompressionAlgorithm.UNCOMPRESSED};
CypherAlgorithm[] cypher = { CypherAlgorithm.CAST5, CypherAlgorithm.AES_128, CypherAlgorithm.AES_192, CypherAlgorithm.AES_256, CypherAlgorithm.BLOWFISH};
KeyAlgorithm keyAlgorithm = KeyAlgorithm.RSA; int keySize = 2048; DateTime expirationDate = DateTime.Now.AddYears(1);
PGPKeyPair keyPair = PGPKeyPair.GenerateKeyPair(keySize, userId, keyAlgorithm, privateKeyPassword, compression, hashing, cypher, _ expirationDate);
// we can call now keyPair.ExportPublicKey or keyPair.ExportPrivateKey } }
Imports System Imports System.IO Imports DidiSoft.Pgp
