using System.IO;
using DidiSoft.Pgp; 
   
class EncryptDemo {
  public void Demo() {
    // create an instance of the library
    PGPLib pgp = new PGPLib();
    
    bool asciiArmor = true;
    bool withIntegrityCheck = true;
  
   using (Stream inputStream = File.OpenRead(@"DataFiles\INPUT.txt"))
   using (Stream publicKeyStream = File.OpenRead(@"DataFiles\public.asc"))
   using (Stream encryptedStream = File.Create(@"DataFiles\OUTPUT.pgp"))
   {
   pgp.EncryptStreamWithPassword(inputStream,
                        "data.txt",
                        publicKeyStream,
                        "alternative_password",
                        encryptedStream, 
                        asciiArmor, 
                        withIntegrityCheck);	
    }
 }
}	

