     var fullKeys = new List<long>();
     fullKeys.AddRange(ks.GetKeys("mike").Select(key => key.KeyId));
      fullKeys.AddRange(ks.GetKeys("james").Select(key => key.KeyId));

	 PGPLib pgp = new PGPLib();
	 string plainText = "Hello World";
	 string encryptedString = pgp.EncryptString(plainText, ks, fullKeys);
	}
}

