 class EncryptFilesWithPasswordDemo 
 {
  public void Demo() 
 {
    PGPLib pgp = new PGPLib();
 
         bool asciiArmor = true;
         bool withIntegrityCheck = true;
  
         // files to be encrypted
         string[] inputFiles =
            new string[] {@"c:\file1.txt",
                          @"c:\file2.dat",
                          @"c:\file3.xls"};

         String password = "my encryption password"

         // encryption output
         string encryptedOutputFile = @"c:\OUTPUT.pgp";
 
         pgp.EncryptFilesWithPassword(inputFiles,
                             password,
                             encryptedOutputFile, 
         	                asciiArmor, 
         	                withIntegrityCheck);	
 	}
 }

