This example shows how to extract the dadata from an OpenPGP signed file

using System;
using DidiSoft.Pgp;

public class ExtractSignedFileDemo
{
 public void Demo()
 {
	// initialize the library
	PGPLib pgp = new PGPLib();
	
	string inputFileLocation = @"c:\INPUT.pgp"; // signed only or clear text signed .pgp data
	string decryptedOutput = @"c:\OUTPUT.txt";

	  pgp.ExtractSignedFile(inputFileLocation,
					  decryptedOutput);		
 }
}

