This example shows how to OpenPGP sign a string message 

using System;
using System.IO;

using DidiSoft.Pgp;

class SignString
{
	public static String Demo()
	{
		String plainString = "Hello World";
		
        // initialize the library 
		PGPLib pgp = new PGPLib();
		
		String signedString = pgp.SignString(plainString,
											 new FileInfo(@"c:\private_key.asc"),
											 "private key password");
		return signedString;
	}
}

Imports System
Imports System.IO

Imports DidiSoft.Pgp

Class SignString
	Public Shared Function Demo() As String
		Dim plainString As String = "Hello World"

        ' initialize the library 
		Dim pgp As New PGPLib()
		
		Dim signedString As String = pgp.SignString(plainString, _
													New FileInfo("c:\private_key.asc"), _
													"private key password")
		Return signedString
	End Function
End Class

This example shows how to extract the message from a digitally signed OpenPGP message
and verify that it is from the expected sender.

using System;
using System.IO;
using DidiSoft.Pgp;

class VerifyString
{
	public static void Demo()
	{
		// obtain an OpenPGP signed message
		String signedString = ...

		// Extract the message and check the validity of the signature
		String plainText;
		PGPLib pgp = new PGPLib();
		SignatureCheckResult signatureCheck = pgp.VerifyString(signedString, 
												new FileInfo(@"DataFiles\public_key.asc"), 
												out plainText);

		// Print the results
		Console.WriteLine("Extracted plain text message is " + plainText);
       if (signatureCheck == SignatureCheckResult.SignatureVerified)
       {
           Console.WriteLine("Signature OK");
       }
       else if (signatureCheck == SignatureCheckResult.SignatureBroken)
       {
           Console.WriteLine("Signature of the message is either broken or forged");
       }
       else if (signatureCheck == SignatureCheckResult.PublicKeyNotMatching)
       {
           Console.WriteLine("The provided public key doesn't match the signature");
       }
       else if (signatureCheck == SignatureCheckResult.NoSignatureFound)
       {
           Console.WriteLine("This message is not digitally signed");
       }
	}
}

Imports System
Imports System.IO
Imports DidiSoft.Pgp

Class VerifyString
	Public Shared Sub Demo()
		' obtain an OpenPGP signed message
		Dim signedString As String = ...

		' Extract the message and check the validity of the signature
		Dim plainText As String
		Dim pgp As New PGPLib()
		Dim signatureCheck As SignatureCheckResult = _
		            pgp.VerifyString(signedString, New FileInfo("DataFiles\public_key.asc"), plainText)

		' Print the results
		Console.WriteLine("Extracted plain text message is " + plainText)
		If signatureCheck = SignatureCheckResult.SignatureVerified Then
			Console.WriteLine("Signature OK")
		ElseIf signatureCheck = SignatureCheckResult.SignatureBroken Then
			Console.WriteLine("Signature of the message is either broken or forged")
		ElseIf signatureCheck = SignatureCheckResult.PublicKeyNotMatching Then
			Console.WriteLine("The provided public key(s) doesn't match the signature")
		ElseIf signatureCheck = SignatureCheckResult.NoSignatureFound Then
			Console.WriteLine("This message is not digitally signed")
		End If
	End Sub
End Class

