 using System;
 using DidiSoft.Pgp;

 public class GenerateKeyPairRSA
 {
    public async void Demo()
    {
        KeyStore ks = new KeyStore(@"DataFiles\key.store", "changeit");
		
        String userId = "demo2@didisoft.com";
        String privateKeyPassword = "changeit";
        CompressionAlgorithm[] compression = {CompressionAlgorithm.ZIP, CompressionAlgorithm.ZLIB};
        HashAlgorithm[] hashingAlgorithm = {HashAlgorithm.MD5, HashAlgorithm.SHA1};
        CypherAlgorithm[] cypher = {CypherAlgorithm.CAST5, CypherAlgorithm.AES_128};
		
        KeyPairInformation newKey = await ks.GenerateKeyPairAsync( 1024, 
                            userId, 
                            KeyAlgorithm.RSA, 
                            privateKeyPassword, 
                            compression, 
                            hashingAlgorithm, 
                            cypher);		
    }
 }

 using System;
 using DidiSoft.Pgp;

 public class GenerateKeyPairRSA
 {
    public async void Demo()
    {
        KeyStore ks = new KeyStore(@"DataFiles\key.store", "changeit");
		
        String userId = "demo2@didisoft.com";
        String privateKeyPassword = "changeit";
        CompressionAlgorithm[] compression = {CompressionAlgorithm.ZIP, CompressionAlgorithm.ZLIB};
        HashAlgorithm[] hashingAlgorithm = {HashAlgorithm.MD5, HashAlgorithm.SHA1};
        CypherAlgorithm[] cypher = {CypherAlgorithm.CAST5, CypherAlgorithm.AES_128};
        
        DateTime validToDate = DateTime.Now.AddDays(365);
        
        KeyPairInformation newKey = await ks.GenerateKeyPairAsync( 1024, 
                            userId, 
                            KeyAlgorithm.RSA, 
                            privateKeyPassword, 
                            compression, 
                            hashingAlgorithm, 
                            cypher,
                            validToDate);		
    }
 }

Imports System Imports DidiSoft.Pgp
Public Class GenerateKeyPairRSA Public Async Sub Demo() Dim ks As New KeyStore("DataFiles\key.store", "changeit")
Dim userId As String = "demo2@didisoft.com" Dim privateKeyPassword As String = "changeit" Dim compression() As CompressionAlgorithm = {CompressionAlgorithm.ZIP, CompressionAlgorithm.ZLIB} Dim hashingAlgorithm() As HashAlgorithm = {HashAlgorithm.MD5, HashAlgorithm.SHA1} Dim cypher() As CypherAlgorithm = {CypherAlgorithm.CAST5, CypherAlgorithm.AES_128}
DateTime validToDate = DateTime.Now.AddDays(365);
Dim newKey As KeyPairInformation = _ Await ks.GenerateKeyPairAsync( 1024, _ userId, _ KeyAlgorithm.RSA, _ privateKeyPassword, _ compression, _ hashingAlgorithm, _ cypher, validToDate) End Sub End Class

 using System;
 using DidiSoft.Pgp;

 public class GenerateKeyPair
 {
    public async void Demo()			
