public class Demo
{
 public async void EncryptDemo()
 {
    // Data file to be encrypted
	using (Stream dataStream = File.OpenRead(@"c:\data\input.txt"))
	// Usually the public key of the recipient
	using (Stream publicKey = File.OpenRead(@"c:\data\recipient_key.asc"))

	usinng (Stream encryptedStream = File.OpenRead(@"c:\data\output.pgp"))
    {
      // Create an instance of the library
	  PGPLib pgp = new PGPLib();
	  // should the output be in binary (false) or ASCII armoured format (true)
	  bool asciiArmour = true;
	  await pgp.EncryptStreamAsync(dataStream, publicKey, encryptedStream, asciiArmour);
	}  
 }
}

