public class Demo
{
 public async void SignDemo()
 {
	string dataFile = "myfile.txt";
	// Usually our private key
	string privateKeyFile = "my_private_key.asc"; 

	string signedFile = "output.pgp";

    // Create an instance of the library
	PGPLib pgp = new PGPLib();
	// Password for the private key
	string privateKeyPassword = "my password";
	// should the output be in binary (false) or ASCII armoured format (true)
	bool asciiArmour = true;
	await DetachedSignFileAsync(dataFile, privateKeyFile, privateKeyPassword, signedFile, asciiArmour);
 }
}

